import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import { FiArrowLeft, FiPackage, FiCalendar, FiMapPin, FiCreditCard, FiTruck } from 'react-icons/fi';

const OrderDetail = () => {
  const { orderId } = useParams();
  const navigate = useNavigate();
  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchOrder();
  }, [orderId]);

  const fetchOrder = async () => {
    try {
      const response = await axios.get(`/api/orders/${orderId}`);
      setOrder(response.data);
    } catch (error) {
      toast.error('Failed to load order details');
      navigate('/orders');
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'pending':
        return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'processing':
        return 'bg-blue-100 text-blue-800 border-blue-200';
      case 'shipped':
        return 'bg-purple-100 text-purple-800 border-purple-200';
      case 'delivered':
        return 'bg-green-100 text-green-800 border-green-200';
      case 'cancelled':
        return 'bg-red-100 text-red-800 border-red-200';
      default:
        return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const getStatusSteps = () => {
    const steps = [
      { key: 'pending', label: 'Order Placed', icon: FiPackage },
      { key: 'processing', label: 'Processing', icon: FiCreditCard },
      { key: 'shipped', label: 'Shipped', icon: FiTruck },
      { key: 'delivered', label: 'Delivered', icon: FiMapPin }
    ];

    const statusOrder = ['pending', 'processing', 'shipped', 'delivered'];
    const currentIndex = statusOrder.indexOf(order?.status);

    return steps.map((step, index) => ({
      ...step,
      completed: index <= currentIndex,
      active: index === currentIndex
    }));
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return (
      <div className="container py-8">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading order details...</p>
        </div>
      </div>
    );
  }

  if (!order) {
    return (
      <div className="container py-8 text-center">
        <h2 className="text-2xl font-bold mb-4">Order not found</h2>
        <Link to="/orders" className="btn btn-primary">
          Back to Orders
        </Link>
      </div>
    );
  }

  const steps = getStatusSteps();

  return (
    <div className="container py-8">
      <button
        onClick={() => navigate('/orders')}
        className="btn btn-secondary mb-6"
      >
        <FiArrowLeft />
        Back to Orders
      </button>

      <div className="mb-8">
        <h1 className="text-3xl font-bold mb-2">
          Order #{order._id.slice(-8).toUpperCase()}
        </h1>
        <div className="flex items-center gap-4 text-gray-600">
          <div className="flex items-center gap-1">
            <FiCalendar size={16} />
            <span>Placed on {formatDate(order.createdAt)}</span>
          </div>
          <span className={`px-3 py-1 rounded-full text-sm font-medium border ${getStatusColor(order.status)}`}>
            {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
          </span>
        </div>
      </div>

      {/* Order Status Timeline */}
      {order.status !== 'cancelled' && (
        <div className="card mb-8">
          <div className="card-header">
            <h2 className="text-xl font-semibold">Order Status</h2>
          </div>
          <div className="card-body">
            <div className="flex justify-between items-center">
              {steps.map((step, index) => {
                const Icon = step.icon;
                return (
                  <div key={step.key} className="flex flex-col items-center flex-1">
                    <div className={`w-12 h-12 rounded-full flex items-center justify-center mb-2 ${
                      step.completed 
                        ? 'bg-blue-500 text-white' 
                        : 'bg-gray-200 text-gray-400'
                    }`}>
                      <Icon size={20} />
                    </div>
                    <span className={`text-sm font-medium ${
                      step.completed ? 'text-blue-600' : 'text-gray-400'
                    }`}>
                      {step.label}
                    </span>
                    {index < steps.length - 1 && (
                      <div className={`absolute h-0.5 w-full top-6 left-1/2 transform -translate-y-1/2 ${
                        step.completed ? 'bg-blue-500' : 'bg-gray-200'
                      }`} style={{ zIndex: -1 }} />
                    )}
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      )}

      <div className="grid grid-2 gap-8">
        {/* Order Items */}
        <div className="space-y-6">
          <div className="card">
            <div className="card-header">
              <h2 className="text-xl font-semibold">Order Items</h2>
            </div>
            <div className="card-body space-y-4">
              {order.items.map(item => (
                <div key={item._id} className="flex gap-4 pb-4 border-b last:border-b-0 last:pb-0">
                  <img
                    src={item.product?.image || '/api/placeholder/80/80'}
                    alt={item.product?.name || 'Product'}
                    className="w-20 h-20 object-cover rounded-lg"
                  />
                  <div className="flex-1">
                    <h3 className="font-semibold mb-1">
                      {item.product?.name || 'Product'}
                    </h3>
                    <p className="text-gray-600 text-sm mb-2">
                      {item.product?.description?.substring(0, 100)}...
                    </p>
                    <div className="flex justify-between items-center">
                      <span className="text-sm text-gray-600">
                        Quantity: {item.quantity}
                      </span>
                      <span className="font-semibold">
                        ${(item.price * item.quantity).toFixed(2)}
                      </span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Order Summary & Details */}
        <div className="space-y-6">
          {/* Order Summary */}
          <div className="card">
            <div className="card-header">
              <h2 className="text-xl font-semibold">Order Summary</h2>
            </div>
            <div className="card-body space-y-3">
              <div className="flex justify-between">
                <span>Subtotal:</span>
                <span>${(order.totalAmount - (order.totalAmount >= 50 ? 0 : 9.99) - (order.totalAmount * 0.08)).toFixed(2)}</span>
              </div>
              <div className="flex justify-between">
                <span>Shipping:</span>
                <span>{order.totalAmount >= 50 ? 'FREE' : '$9.99'}</span>
              </div>
              <div className="flex justify-between">
                <span>Tax:</span>
                <span>${(order.totalAmount * 0.08).toFixed(2)}</span>
              </div>
              <hr />
              <div className="flex justify-between text-lg font-bold">
                <span>Total:</span>
                <span className="text-blue-500">${order.totalAmount.toFixed(2)}</span>
              </div>
            </div>
          </div>

          {/* Shipping Address */}
          <div className="card">
            <div className="card-header">
              <h2 className="text-xl font-semibold flex items-center gap-2">
                <FiMapPin />
                Shipping Address
              </h2>
            </div>
            <div className="card-body">
              <div className="text-gray-700">
                <p className="font-medium">
                  {order.shippingAddress.firstName} {order.shippingAddress.lastName}
                </p>
                <p>{order.shippingAddress.address}</p>
                <p>
                  {order.shippingAddress.city}, {order.shippingAddress.state} {order.shippingAddress.zipCode}
                </p>
                <p>{order.shippingAddress.country}</p>
              </div>
            </div>
          </div>

          {/* Order Actions */}
          <div className="card">
            <div className="card-body">
              <div className="space-y-3">
                {order.status === 'pending' && (
                  <button className="btn btn-danger w-full">
                    Cancel Order
                  </button>
                )}
                
                {order.status === 'delivered' && (
                  <div className="space-y-2">
                    <button className="btn btn-primary w-full">
                      Reorder Items
                    </button>
                    <button className="btn btn-secondary w-full">
                      Leave Review
                    </button>
                  </div>
                )}
                
                <button className="btn btn-secondary w-full">
                  Download Invoice
                </button>
                
                <Link to="/support" className="btn btn-secondary w-full text-center">
                  Contact Support
                </Link>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OrderDetail;
