import React, { useEffect, useState } from 'react';
import Loader from './Loader';
import axios from 'axios';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import TextField from '@mui/material/TextField';
import MenuItem from '@mui/material/MenuItem';
import Button from '@mui/material/Button';

function EditOrder({ orderId }) {
  const [order, setOrder] = useState(null);
  const [status, setStatus] = useState('pending');
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    axios.get(`http://localhost:5000/api/orders/${orderId}`)
      .then(res => {
        setOrder(res.data);
        setStatus(res.data.status);
      })
      .catch(() => {
        setError('Failed to fetch order');
        setOrder(null);
      });
  }, [orderId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:5000/api/orders/${orderId}`, { status });
      setSuccess('Order updated!');
      setError('');
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to update order');
      setSuccess('');
    }
  };

  if (!order && !error) return <Loader />;

  return (
    <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', flexDirection: 'column', gap: 2, maxWidth: 400, mx: 'auto' }}>
      <Typography variant="h5" color="primary">Edit Order</Typography>
      {error && <Typography color="error">{error}</Typography>}
      <TextField select label="Status" value={status} onChange={e => setStatus(e.target.value)} fullWidth>
        <MenuItem value="pending">Pending</MenuItem>
        <MenuItem value="completed">Completed</MenuItem>
        <MenuItem value="cancelled">Cancelled</MenuItem>
      </TextField>
      <Button type="submit" variant="contained" color="primary">Update</Button>
      {success && <Typography color="success.main">{success}</Typography>}
    </Box>
  );
}

export default EditOrder;
