import React, { useState, useEffect } from 'react';
import { Link, useSearchParams } from 'react-router-dom';
import { useCart } from '../contexts/CartContext';
import axios from 'axios';
import '../products.css';

function Products() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [pagination, setPagination] = useState({
    currentPage: 1,
    totalPages: 1,
    totalProducts: 0,
    hasNextPage: false,
    hasPrevPage: false
  });
  const [filters, setFilters] = useState({
    category: '',
    minPrice: '',
    maxPrice: '',
    sortBy: 'name'
  });
  const [searchParams] = useSearchParams();
  const { addToCart } = useCart();

  const categories = [
    'Electronics',
    'Clothing',
    'Books',
    'Home & Garden',
    'Sports',
    'Beauty',
    'Automotive',
    'Toys'
  ];

  const sortOptions = [
    { value: 'name', label: 'Name A-Z' },
    { value: '-name', label: 'Name Z-A' },
    { value: 'price', label: 'Price: Low to High' },
    { value: '-price', label: 'Price: High to Low' },
    { value: '-createdAt', label: 'Newest First' }
  ];

  useEffect(() => {
    const fetchProducts = async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams();
      
      // Add pagination params
      params.append('page', pagination.currentPage.toString());
      params.append('limit', '10');
      
      // Add filters to params
      if (filters.category) params.append('category', filters.category);
      if (filters.minPrice) params.append('minPrice', filters.minPrice);
      if (filters.maxPrice) params.append('maxPrice', filters.maxPrice);
      if (filters.sortBy) params.append('sort', filters.sortBy);
      
      // Add search query if present
      const searchQuery = searchParams.get('q');
      if (searchQuery) params.append('search', searchQuery);

      const url = `/api/products?${params.toString()}`;
      console.log('Fetching products from:', url);
      
      const response = await axios.get(url);
      console.log('Raw response:', response);
      console.log('Response data:', response.data);
      
      // Handle paginated response
      if (response.data.products && response.data.pagination) {
        setProducts(response.data.products);
        setPagination(response.data.pagination);
        console.log('Products set to:', response.data.products.length, 'items');
        console.log('Pagination:', response.data.pagination);
      } else {
        // Handle non-paginated response (fallback)
        const productsData = Array.isArray(response.data) ? response.data : response.data.products || [];
        setProducts(productsData);
        setPagination({
          currentPage: 1,
          totalPages: 1,
          totalProducts: productsData.length,
          hasNextPage: false,
          hasPrevPage: false
        });
        console.log('Products set to:', productsData.length, 'items (fallback)');
      }
    } catch (error) {
      console.error('Error fetching products:', error);
      setProducts([]); // Ensure products is always an array
      setPagination({
        currentPage: 1,
        totalPages: 1,
        totalProducts: 0,
        hasNextPage: false,
        hasPrevPage: false
      });
    } finally {
      setLoading(false);
    }
    };

    fetchProducts();
  }, [filters, searchParams, pagination.currentPage]);

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value
    }));
    // Reset to page 1 when filters change
    setPagination(prev => ({
      ...prev,
      currentPage: 1
    }));
  };

  const clearFilters = () => {
    setFilters({
      category: '',
      minPrice: '',
      maxPrice: '',
      sortBy: 'name'
    });
    // Reset to page 1 when filters are cleared
    setPagination(prev => ({
      ...prev,
      currentPage: 1
    }));
  };

  const handlePageChange = (newPage) => {
    if (newPage >= 1 && newPage <= pagination.totalPages) {
      setPagination(prev => ({
        ...prev,
        currentPage: newPage
      }));
      // Scroll to top when page changes
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
  };

  const handleAddToCart = (product) => {
    addToCart({
      id: product._id,
      name: product.name,
      price: product.price,
      image: product.image,
      quantity: 1
    });
  };

  const searchQuery = searchParams.get('q');

  return (
    <div className="products-page">
      <div className="container">
        {searchQuery && (
          <div className="search-results-header">
            <h1>Search results for "{searchQuery}"</h1>
            <p>{Array.isArray(products) ? products.length : 0} results found</p>
          </div>
        )}

        <div className="products-layout">
          {/* Sidebar Filters */}
          <aside className="filters-sidebar">
            <div className="filters-header">
              <h3>Filters</h3>
              <button onClick={clearFilters} className="clear-filters">
                Clear All
              </button>
            </div>

            {/* Category Filter */}
            <div className="filter-group">
              <h4>Category</h4>
              <select
                value={filters.category}
                onChange={(e) => handleFilterChange('category', e.target.value)}
                className="filter-select"
              >
                <option value="">All Categories</option>
                {categories.map(category => (
                  <option key={category} value={category}>
                    {category}
                  </option>
                ))}
              </select>
            </div>

            {/* Price Filter */}
            <div className="filter-group">
              <h4>Price Range</h4>
              <div className="price-inputs">
                <input
                  type="number"
                  placeholder="Min"
                  value={filters.minPrice}
                  onChange={(e) => handleFilterChange('minPrice', e.target.value)}
                  className="price-input"
                />
                <span>to</span>
                <input
                  type="number"
                  placeholder="Max"
                  value={filters.maxPrice}
                  onChange={(e) => handleFilterChange('maxPrice', e.target.value)}
                  className="price-input"
                />
              </div>
            </div>

            {/* Customer Reviews */}
            <div className="filter-group">
              <h4>Customer Reviews</h4>
              <div className="rating-filters">
                <label className="rating-filter">
                  <input type="checkbox" />
                  <span className="stars">★★★★★</span> & Up
                </label>
                <label className="rating-filter">
                  <input type="checkbox" />
                  <span className="stars">★★★★☆</span> & Up
                </label>
                <label className="rating-filter">
                  <input type="checkbox" />
                  <span className="stars">★★★☆☆</span> & Up
                </label>
              </div>
            </div>
          </aside>

          {/* Main Content */}
          <main className="products-main">
            {/* Sort and Results Info */}
            <div className="products-header">
              <div className="results-info">
                <span>
                  {pagination.totalProducts > 0 
                    ? `Showing ${((pagination.currentPage - 1) * 10) + 1}-${Math.min(pagination.currentPage * 10, pagination.totalProducts)} of ${pagination.totalProducts} results`
                    : '0 results'
                  }
                </span>
              </div>
              <div className="sort-controls">
                <label>Sort by:</label>
                <select
                  value={filters.sortBy}
                  onChange={(e) => handleFilterChange('sortBy', e.target.value)}
                  className="sort-select"
                >
                  {sortOptions.map(option => (
                    <option key={option.value} value={option.value}>
                      {option.label}
                    </option>
                  ))}
                </select>
              </div>
            </div>

            {/* Products Grid */}
            {loading ? (
              <div className="loading">
                <div className="loading-spinner"></div>
                <p>Loading products...</p>
              </div>
            ) : !Array.isArray(products) || products.length === 0 ? (
              <div className="no-products">
                <h3>No products found</h3>
                <p>Try adjusting your filters or search terms</p>
                {console.log('No products to display. Products state:', products, 'Is array:', Array.isArray(products), 'Length:', products?.length)}
              </div>
            ) : (
              <div className="products-grid">
                {console.log('Rendering products grid. Products:', products.length, 'items')}
                {products.map((product) => {
                  console.log('Mapping product:', product.name, product._id);
                  return (
                    <div key={product._id} className="product-card">
                      <Link to={`/product/${product._id}`} className="product-link">
                        <div className="product-image">
                          <img 
                            src={product.image || 'https://via.placeholder.com/250x250'} 
                            alt={product.name}
                          />
                          {product.discount && (
                            <div className="discount-badge">
                              -{product.discount}%
                            </div>
                          )}
                        </div>
                      </Link>
                      
                      <div className="product-info">
                      <Link to={`/product/${product._id}`}>
                        <h3 className="product-name">{product.name}</h3>
                      </Link>
                      
                      <div className="product-rating">
                        <div className="stars">
                          {'★'.repeat(4)}{'☆'.repeat(1)}
                        </div>
                        <span className="rating-count">(127)</span>
                      </div>
                      
                      <div className="product-price">
                        <span className="current-price">₹{product.price}</span>
                        {product.originalPrice && (
                          <span className="original-price">₹{product.originalPrice}</span>
                        )}
                      </div>
                      
                      <div className="product-shipping">
                        <span className="shipping-text">FREE Shipping</span>
                      </div>
                      
                      <div className="product-actions">
                        <button
                          onClick={() => handleAddToCart(product)}
                          className="add-to-cart-btn"
                        >
                          Add to Cart
                        </button>
                        <button className="wishlist-btn">
                          ♡
                        </button>
                      </div>
                    </div>
                  </div>
                  );
                })}
              </div>
            )}

            {/* Pagination */}
            {!loading && pagination.totalPages > 1 && (
              <div className="pagination-container">
                <div className="pagination">
                  {/* Previous Page */}
                  <button
                    onClick={() => handlePageChange(pagination.currentPage - 1)}
                    disabled={!pagination.hasPrevPage}
                    className={`pagination-btn ${!pagination.hasPrevPage ? 'disabled' : ''}`}
                  >
                    ← Previous
                  </button>

                  {/* Page Numbers */}
                  <div className="page-numbers">
                    {/* First page */}
                    {pagination.currentPage > 3 && (
                      <>
                        <button
                          onClick={() => handlePageChange(1)}
                          className="pagination-btn"
                        >
                          1
                        </button>
                        {pagination.currentPage > 4 && <span className="pagination-dots">...</span>}
                      </>
                    )}

                    {/* Pages around current */}
                    {Array.from({ length: Math.min(5, pagination.totalPages) }, (_, i) => {
                      let pageNum;
                      if (pagination.totalPages <= 5) {
                        pageNum = i + 1;
                      } else if (pagination.currentPage <= 3) {
                        pageNum = i + 1;
                      } else if (pagination.currentPage >= pagination.totalPages - 2) {
                        pageNum = pagination.totalPages - 4 + i;
                      } else {
                        pageNum = pagination.currentPage - 2 + i;
                      }

                      if (pageNum > 0 && pageNum <= pagination.totalPages) {
                        return (
                          <button
                            key={pageNum}
                            onClick={() => handlePageChange(pageNum)}
                            className={`pagination-btn ${pageNum === pagination.currentPage ? 'active' : ''}`}
                          >
                            {pageNum}
                          </button>
                        );
                      }
                      return null;
                    })}

                    {/* Last page */}
                    {pagination.currentPage < pagination.totalPages - 2 && (
                      <>
                        {pagination.currentPage < pagination.totalPages - 3 && <span className="pagination-dots">...</span>}
                        <button
                          onClick={() => handlePageChange(pagination.totalPages)}
                          className="pagination-btn"
                        >
                          {pagination.totalPages}
                        </button>
                      </>
                    )}
                  </div>

                  {/* Next Page */}
                  <button
                    onClick={() => handlePageChange(pagination.currentPage + 1)}
                    disabled={!pagination.hasNextPage}
                    className={`pagination-btn ${!pagination.hasNextPage ? 'disabled' : ''}`}
                  >
                    Next →
                  </button>
                </div>

                {/* Page Info */}
                <div className="pagination-info">
                  Page {pagination.currentPage} of {pagination.totalPages}
                </div>
              </div>
            )}
          </main>
        </div>
      </div>
    </div>
  );
}

export default Products;
