import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

function Navbar() {
  const { user, logout } = useAuth();
  const isAdmin = user?.role === 'admin';

  return (
    <header style={{background: '#0f172a', color: '#fff', position: 'sticky', top: 0, zIndex: 50}}>
      <div className="container" style={{display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '14px 0'}}>
        <Link to="/" style={{color: '#fff', textDecoration: 'none', fontWeight: 700, letterSpacing: '0.5px'}}>CloudCommerce</Link>
        <nav style={{display: 'flex', alignItems: 'center', gap: '1rem'}}>
          <Link to="/products" style={{color: '#cbd5e1', textDecoration: 'none'}}>Products</Link>
          {isAdmin && <Link to="/add-product" style={{color: '#cbd5e1', textDecoration: 'none'}}>Add Product</Link>}
          {isAdmin && <Link to="/orders" style={{color: '#cbd5e1', textDecoration: 'none'}}>Orders</Link>}
          {isAdmin && <Link to="/admin" style={{color: '#cbd5e1', textDecoration: 'none'}}>Admin</Link>}
        </nav>
        <div style={{display: 'flex', alignItems: 'center', gap: '0.75rem'}}>
          {!user ? (
            <>
              <Link to="/login" className="btn btn-secondary btn-sm">Login</Link>
              <Link to="/register" className="btn btn-primary btn-sm">Register</Link>
            </>
          ) : (
            <>
              <span className="text-sm" style={{color: '#cbd5e1'}}>Signed in</span>
              <button className="btn btn-secondary btn-sm" onClick={logout}>Logout</button>
            </>
          )}
        </div>
      </div>
    </header>
  );
}

export default Navbar;
