import React, { useEffect, useState } from 'react';
import Loader from './Loader';
import axios from 'axios';

function EditProduct({ productId }) {
  const [product, setProduct] = useState(null);
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [description, setDescription] = useState('');
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    axios.get(`/api/products/${productId}`)
      .then(res => {
        setProduct(res.data);
        setName(res.data.name);
        setPrice(res.data.price);
        setDescription(res.data.description);
      })
      .catch(() => {
        setError('Failed to fetch product');
        setProduct(null);
      });
  }, [productId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`/api/products/${productId}`, { name, price, description });
      setSuccess('Product updated!');
      setError('');
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to update product');
      setSuccess('');
    }
  };

  if (!product && !error) return <Loader />;

  return (
    <div className="card">
      <div className="card-header">
        <h2 className="text-2xl">Edit Product</h2>
      </div>
      <div className="card-body">
        {error && <div className="error">{error}</div>}
        {success && <div className="success">{success}</div>}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="form-label">Name</label>
            <input className="form-input" type="text" value={name} onChange={e => setName(e.target.value)} required />
          </div>
          <div className="form-group">
            <label className="form-label">Price</label>
            <input className="form-input" type="number" value={price} onChange={e => setPrice(e.target.value)} required />
          </div>
          <div className="form-group">
            <label className="form-label">Description</label>
            <textarea className="form-input form-textarea" value={description} onChange={e => setDescription(e.target.value)} rows={3} />
          </div>
          <div className="flex justify-end">
            <button className="btn btn-primary" type="submit">Update</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default EditProduct;
