import React, { useState } from 'react';
import axios from 'axios';

function AddProduct() {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [description, setDescription] = useState('');
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/products', { name, price, description });
      setSuccess('Product added!');
      setError('');
      setName('');
      setPrice('');
      setDescription('');
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to add product');
      setSuccess('');
    }
  };

  return (
    <div className="card">
      <div className="card-header">
        <h2 className="text-2xl">Add Product</h2>
      </div>
      <div className="card-body">
        {success && <div className="success">{success}</div>}
        {error && <div className="error">{error}</div>}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="form-label">Name</label>
            <input className="form-input" type="text" placeholder="Product name" value={name} onChange={e => setName(e.target.value)} required />
          </div>
          <div className="form-group">
            <label className="form-label">Price</label>
            <input className="form-input" type="number" placeholder="Product price" value={price} onChange={e => setPrice(e.target.value)} required />
          </div>
          <div className="form-group">
            <label className="form-label">Description</label>
            <textarea className="form-input form-textarea" placeholder="Product description" value={description} onChange={e => setDescription(e.target.value)} rows={3} />
          </div>
          <div className="flex justify-end">
            <button className="btn btn-primary" type="submit">Add Product</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default AddProduct;
