import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../contexts/CartContext';
import { FiMinus, FiPlus, FiTrash2, FiShoppingBag, FiArrowRight } from 'react-icons/fi';

const Cart = () => {
  const { cartItems, updateQuantity, removeFromCart, getCartTotal, clearCart } = useCart();
  const navigate = useNavigate();

  if (cartItems.length === 0) {
    return (
      <div className="container py-12 text-center">
        <FiShoppingBag size={64} className="mx-auto text-gray-400 mb-4" />
        <h2 className="text-2xl font-bold mb-4">Your cart is empty</h2>
        <p className="text-gray-600 mb-8">Looks like you haven't added any items to your cart yet.</p>
        <Link to="/products" className="btn btn-primary">
          Continue Shopping
        </Link>
      </div>
    );
  }

  const handleQuantityChange = (productId, newQuantity) => {
    if (newQuantity < 1) {
      removeFromCart(productId);
    } else {
      updateQuantity(productId, newQuantity);
    }
  };

  const handleCheckout = () => {
    navigate('/checkout');
  };

  return (
    <div className="container py-8">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold">Shopping Cart</h1>
        <button
          onClick={clearCart}
          className="btn btn-secondary"
        >
          Clear Cart
        </button>
      </div>

      <div className="grid md:grid-cols-2 gap-8">
        {/* Cart Items */}
        <div className="space-y-4">
          {cartItems.map(item => (
            <div key={item._id} className="card">
              <div className="card-body">
                <div className="flex gap-4">
                  <img
                    src={item.image || '/api/placeholder/100/100'}
                    alt={item.name}
                    className="w-20 h-20 object-cover rounded-lg"
                  />
                  
                  <div className="flex-1">
                    <h3 className="text-lg font-semibold mb-2">{item.name}</h3>
                    <p className="text-gray-600 text-sm mb-2">
                      {item.description?.substring(0, 100)}...
                    </p>
                    <p className="text-xl font-bold text-blue-500">
                      ${item.price.toFixed(2)}
                    </p>
                  </div>
                  
                  <div className="flex flex-col items-end gap-2">
                    <button
                      onClick={() => removeFromCart(item._id)}
                      className="text-red-500 hover:text-red-700 p-1"
                      title="Remove item"
                    >
                      <FiTrash2 />
                    </button>
                    
                    <div className="flex items-center border border-gray-300 rounded-lg">
                      <button
                        onClick={() => handleQuantityChange(item._id, item.quantity - 1)}
                        className="p-2 hover:bg-gray-100"
                      >
                        <FiMinus size={14} />
                      </button>
                      <span className="px-3 py-2 border-x border-gray-300 min-w-0 text-center" style={{minWidth: '50px'}}>
                        {item.quantity}
                      </span>
                      <button
                        onClick={() => handleQuantityChange(item._id, item.quantity + 1)}
                        disabled={item.quantity >= item.stock}
                        className="p-2 hover:bg-gray-100 disabled:opacity-50 disabled:cursor-not-allowed"
                      >
                        <FiPlus size={14} />
                      </button>
                    </div>
                    
                    <p className="text-sm text-gray-500">
                      Subtotal: ${(item.price * item.quantity).toFixed(2)}
                    </p>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Order Summary */}
        <div className="card h-fit">
          <div className="card-header">
            <h2 className="text-xl font-semibold">Order Summary</h2>
          </div>
          
          <div className="card-body space-y-4">
            <div className="flex justify-between">
              <span>Items ({cartItems.reduce((sum, item) => sum + item.quantity, 0)}):</span>
              <span>${getCartTotal().toFixed(2)}</span>
            </div>
            
            <div className="flex justify-between">
              <span>Shipping:</span>
              <span className="text-green-500">
                {getCartTotal() >= 50 ? 'FREE' : '$9.99'}
              </span>
            </div>
            
            <div className="flex justify-between">
              <span>Tax:</span>
              <span>${(getCartTotal() * 0.08).toFixed(2)}</span>
            </div>
            
            <hr />
            
            <div className="flex justify-between text-xl font-bold">
              <span>Total:</span>
              <span className="text-blue-500">
                ${(
                  getCartTotal() + 
                  (getCartTotal() >= 50 ? 0 : 9.99) + 
                  (getCartTotal() * 0.08)
                ).toFixed(2)}
              </span>
            </div>
            
            {getCartTotal() < 50 && (
              <p className="text-sm text-gray-600 bg-yellow-50 p-3 rounded-lg">
                Add ${(50 - getCartTotal()).toFixed(2)} more to get free shipping!
              </p>
            )}
          </div>
          
          <div className="card-footer space-y-3">
            <button
              onClick={handleCheckout}
              className="btn btn-primary btn-lg w-full"
            >
              Proceed to Checkout
              <FiArrowRight />
            </button>
            
            <Link
              to="/products"
              className="btn btn-secondary w-full text-center"
            >
              Continue Shopping
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Cart;
