import React from 'react';
import { Link } from 'react-router-dom';
import DeleteProduct from './DeleteProduct';

function ProductListItem({ product }) {
  return (
    <li className="flex justify-between items-center p-4 bg-gray-50 rounded hover:shadow-lg transition-shadow">
      <div>
        <div className="font-semibold">{product.name} <span className="text-blue-600">${product.price}</span></div>
        {product.description && <div className="text-gray-600 text-sm mt-1">{product.description}</div>}
      </div>
      <div className="space-x-2">
        <Link to={`/edit-product/${product._id}`} className="btn btn-secondary btn-sm">Edit</Link>
        <DeleteProduct productId={product._id} onDeleted={() => window.location.reload()} />
      </div>
    </li>
  );
}

export default ProductListItem;
