import React from 'react';
import { Link } from 'react-router-dom';
import DeleteProduct from './DeleteProduct';

function ProductListItem({ product }) {
  return (
    <li style={{display: 'flex', justifyContent: 'space-between', alignItems: 'center'}}>
      <span>
        <strong>{product.name}</strong> <span style={{color: '#1976d2'}}>${product.price}</span>
        {product.description && <span style={{marginLeft: '1em', color: '#555'}}>{product.description}</span>}
      </span>
      <span>
        <Link to={`/edit-product/${product._id}`} style={{marginRight: '1em'}}>Edit</Link>
        <DeleteProduct productId={product._id} onDeleted={() => window.location.reload()} />
      </span>
    </li>
  );
}

export default ProductListItem;
