import React from 'react';
import { Link } from 'react-router-dom';
import DeleteOrder from './DeleteOrder';

function OrderListItem({ order }) {
  return (
    <li style={{display: 'flex', justifyContent: 'space-between', alignItems: 'center'}}>
      <span>
        <strong>Order #{order._id}</strong> <span style={{color: '#1976d2'}}>Status: {order.status}</span> <span style={{marginLeft: '1em', color: '#555'}}>Total: ${order.total}</span>
      </span>
      <span>
        <Link to={`/edit-order/${order._id}`} style={{marginRight: '1em'}}>Edit</Link>
        <DeleteOrder orderId={order._id} onDeleted={() => window.location.reload()} />
      </span>
    </li>
  );
}

export default OrderListItem;
