import React, { useState } from 'react';
import axios from 'axios';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';

function Register() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/auth/register', { username, email, password });
      setSuccess('Registration successful!');
      setError('');
    } catch (err) {
      setError(err.response?.data?.error || 'Registration failed');
      setSuccess('');
    }
  };

  return (
    <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', flexDirection: 'column', gap: 2, maxWidth: 400, mx: 'auto' }}>
      <Typography variant="h5" color="primary">Register</Typography>
      <TextField label="Username" value={username} onChange={e => setUsername(e.target.value)} required fullWidth />
      <TextField label="Email" type="email" value={email} onChange={e => setEmail(e.target.value)} required fullWidth />
      <TextField label="Password" type="password" value={password} onChange={e => setPassword(e.target.value)} required fullWidth />
      <Button type="submit" variant="contained" color="primary">Register</Button>
      {error && <Typography color="error">{error}</Typography>}
      {success && <Typography color="success.main">{success}</Typography>}
    </Box>
  );
}

export default Register;
