import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { 
  Grid, 
  Card, 
  CardContent, 
  CardMedia, 
  CardActions, 
  Typography, 
  Button, 
  Rating,
  Box,
  Chip
} from '@mui/material';
import { Link } from 'react-router-dom';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import FavoriteIcon from '@mui/icons-material/Favorite';
import VisibilityIcon from '@mui/icons-material/Visibility';

function ProductList() {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5000/api/products')
      .then(res => setProducts(res.data))
      .catch(() => {
        setProducts([]);
        setError('Failed to load products');
      });
  }, []);

  return (
    <div>
      <Typography variant="h4" color="primary" gutterBottom>Products</Typography>
      {error && <Typography color="error" align="center">{error}</Typography>}
      <Grid container spacing={3}>
        {products.length === 0 ? (
          <Grid item xs={12} align="center">
            <Typography>No products found.</Typography>
          </Grid>
        ) : (
          products.map(product => (
            <Grid item xs={12} sm={6} md={4} key={product._id}>
              <Card>
                <CardContent>
                  <Typography variant="h6">{product.name}</Typography>
                  <Typography color="text.secondary">${product.price}</Typography>
                  {product.description && <Typography variant="body2" color="text.secondary">{product.description}</Typography>}
                </CardContent>
                <CardActions>
                  <Button size="small" component={Link} to={`/edit-product/${product._id}`}>Edit</Button>
                  <Button size="small" color="error" onClick={async () => {
                    await axios.delete(`http://localhost:5000/api/products/${product._id}`);
                    setProducts(products.filter(p => p._id !== product._id));
                  }}>Delete</Button>
                </CardActions>
              </Card>
            </Grid>
          ))
        )}
      </Grid>
    </div>
  );
}

export default ProductList;
