import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import ProductCard from '../components/ProductCard';
import { FiArrowRight, FiShoppingBag, FiTruck, FiShield, FiHeadphones } from 'react-icons/fi';

const Home = () => {
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchFeaturedProducts();
  }, []);

  const fetchFeaturedProducts = async () => {
    try {
      const response = await axios.get('/api/products');
      // Get first 6 products as featured
      setFeaturedProducts(response.data.slice(0, 6));
    } catch (error) {
      console.error('Error fetching products:', error);
    } finally {
      setLoading(false);
    }
  };

  const features = [
    {
      icon: <FiShoppingBag size={32} />,
      title: 'Wide Selection',
      description: 'Thousands of products across multiple categories'
    },
    {
      icon: <FiTruck size={32} />,
      title: 'Fast Delivery',
      description: 'Free shipping on orders over $50'
    },
    {
      icon: <FiShield size={32} />,
      title: 'Secure Shopping',
      description: '100% secure payment processing'
    },
    {
      icon: <FiHeadphones size={32} />,
      title: '24/7 Support',
      description: 'Customer support available around the clock'
    }
  ];

  return (
    <div>
      {/* Hero Section */}
      <section className="bg-gradient-to-r from-blue-600 to-purple-600 text-white py-20">
        <div className="container text-center">
          <h1 className="text-5xl font-bold mb-6">
            Welcome to CloudCommerce
          </h1>
          <p className="text-xl mb-8 max-w-2xl mx-auto">
            Discover amazing products at unbeatable prices. Shop with confidence 
            and enjoy fast, secure delivery to your doorstep.
          </p>
          <div className="flex flex-col sm:flex-row gap-4 justify-center">
            <Link to="/products" className="btn btn-lg bg-white text-blue-600 hover:bg-gray-100">
              Shop Now
              <FiArrowRight />
            </Link>
            <Link to="/register" className="btn btn-lg border-2 border-white text-white hover:bg-white hover:text-blue-600">
              Sign Up Today
            </Link>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="py-16 bg-white">
        <div className="container">
          <h2 className="text-3xl font-bold text-center mb-12">Why Choose CloudCommerce?</h2>
          <div className="grid grid-4 gap-8">
            {features.map((feature, index) => (
              <div key={index} className="text-center">
                <div className="inline-flex items-center justify-center w-16 h-16 bg-blue-100 text-blue-500 rounded-full mb-4">
                  {feature.icon}
                </div>
                <h3 className="text-xl font-semibold mb-2">{feature.title}</h3>
                <p className="text-gray-600">{feature.description}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Featured Products Section */}
      <section className="py-16 bg-gray-50">
        <div className="container">
          <div className="flex justify-between items-center mb-12">
            <h2 className="text-3xl font-bold">Featured Products</h2>
            <Link to="/products" className="btn btn-primary">
              View All Products
              <FiArrowRight />
            </Link>
          </div>
          
          {loading ? (
            <div className="loading">
              <div className="spinner"></div>
            </div>
          ) : (
            <div className="grid grid-3 gap-6">
              {featuredProducts.map(product => (
                <ProductCard key={product._id} product={product} />
              ))}
            </div>
          )}
        </div>
      </section>

      {/* CTA Section */}
      <section className="py-16 bg-gray-900 text-white">
        <div className="container text-center">
          <h2 className="text-3xl font-bold mb-4">Ready to Start Shopping?</h2>
          <p className="text-xl mb-8 text-gray-300">
            Join thousands of satisfied customers and discover your next favorite product.
          </p>
          <Link to="/register" className="btn btn-lg btn-primary">
            Create Your Account
            <FiArrowRight />
          </Link>
        </div>
      </section>
    </div>
  );
};

export default Home;
