import React from 'react';
import { Link } from 'react-router-dom';
import DeleteOrder from './DeleteOrder';

function OrderListItem({ order }) {
  return (
    <li className="flex justify-between items-center p-4 bg-gray-50 rounded hover:shadow-lg transition-shadow">
      <div>
        <div className="font-semibold">Order #{order._id}</div>
        <div className="text-sm text-gray-600 mt-1">Status: <span className="text-blue-600">{order.status}</span> Â· Total: ${order.total}</div>
      </div>
      <div className="space-x-2">
        <Link to={`/edit-order/${order._id}`} className="btn btn-secondary btn-sm">Edit</Link>
        <DeleteOrder orderId={order._id} onDeleted={() => window.location.reload()} />
      </div>
    </li>
  );
}

export default OrderListItem;
