import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useCart } from '../contexts/CartContext';
import { useAuth } from '../contexts/AuthContext';
import { FiShoppingCart, FiMinus, FiPlus, FiArrowLeft } from 'react-icons/fi';
import { toast } from 'react-toastify';

const ProductDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart, isInCart, getCartItem } = useCart();
  const { isAuthenticated } = useAuth();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    fetchProduct();
  }, [id]);

  const fetchProduct = async () => {
    try {
      const response = await axios.get(`/api/products/${id}`);
      setProduct(response.data);
    } catch (error) {
      console.error('Error fetching product:', error);
      toast.error('Product not found');
      navigate('/products');
    } finally {
      setLoading(false);
    }
  };

  const handleAddToCart = () => {
    addToCart(product, quantity);
    setQuantity(1);
  };

  const incrementQuantity = () => {
    if (quantity < product.stock) {
      setQuantity(quantity + 1);
    }
  };

  const decrementQuantity = () => {
    if (quantity > 1) {
      setQuantity(quantity - 1);
    }
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="container py-8 text-center">
        <h2 className="text-2xl font-bold mb-4">Product not found</h2>
        <button onClick={() => navigate('/products')} className="btn btn-primary">
          Back to Products
        </button>
      </div>
    );
  }

  const cartItem = getCartItem(product._id);
  const totalInCart = cartItem ? cartItem.quantity : 0;
  const maxQuantity = product.stock - totalInCart;

  return (
    <div className="container py-8">
      <button
        onClick={() => navigate('/products')}
        className="btn btn-secondary mb-6"
      >
        <FiArrowLeft />
        Back to Products
      </button>

      <div className="grid grid-2 gap-8">
        {/* Product Image */}
        <div className="card">
          <img
            src={product.image || '/api/placeholder/500/400'}
            alt={product.name}
            className="w-full h-96 object-cover rounded-lg"
          />
        </div>

        {/* Product Info */}
        <div>
          <h1 className="text-3xl font-bold mb-4">{product.name}</h1>
          <p className="text-4xl font-bold text-blue-500 mb-6">
            ${product.price.toFixed(2)}
          </p>
          
          <div className="mb-6">
            <h3 className="text-lg font-semibold mb-2">Description</h3>
            <p className="text-gray-600 leading-relaxed">
              {product.description || 'No description available.'}
            </p>
          </div>

          <div className="mb-6">
            <div className="flex items-center gap-4 mb-4">
              <span className="text-lg font-semibold">Stock:</span>
              <span className={`px-3 py-1 rounded-full text-sm font-medium ${
                product.stock > 0 
                  ? 'bg-green-100 text-green-800' 
                  : 'bg-red-100 text-red-800'
              }`}>
                {product.stock > 0 ? `${product.stock} available` : 'Out of stock'}
              </span>
            </div>
            
            {totalInCart > 0 && (
              <p className="text-sm text-gray-600 mb-4">
                You have {totalInCart} of this item in your cart
              </p>
            )}
          </div>

          {isAuthenticated() ? (
            product.stock > 0 && maxQuantity > 0 ? (
              <div className="space-y-4">
                <div className="flex items-center gap-4">
                  <span className="text-lg font-semibold">Quantity:</span>
                  <div className="flex items-center border border-gray-300 rounded-lg">
                    <button
                      onClick={decrementQuantity}
                      disabled={quantity <= 1}
                      className="p-2 hover:bg-gray-100 disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                      <FiMinus />
                    </button>
                    <span className="px-4 py-2 border-x border-gray-300 min-w-[60px] text-center">
                      {quantity}
                    </span>
                    <button
                      onClick={incrementQuantity}
                      disabled={quantity >= maxQuantity}
                      className="p-2 hover:bg-gray-100 disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                      <FiPlus />
                    </button>
                  </div>
                  <span className="text-sm text-gray-500">
                    Max: {maxQuantity}
                  </span>
                </div>
                
                <button
                  onClick={handleAddToCart}
                  className="btn btn-primary btn-lg w-full"
                >
                  <FiShoppingCart />
                  Add to Cart - ${(product.price * quantity).toFixed(2)}
                </button>
              </div>
            ) : (
              <div className="text-center py-4">
                <p className="text-gray-600 mb-4">
                  {product.stock === 0 
                    ? 'This product is out of stock' 
                    : 'You have the maximum quantity in your cart'
                  }
                </p>
                <button
                  onClick={() => navigate('/cart')}
                  className="btn btn-secondary"
                >
                  View Cart
                </button>
              </div>
            )
          ) : (
            <div className="text-center py-4">
              <p className="text-gray-600 mb-4">Please login to purchase this product</p>
              <button
                onClick={() => navigate('/login')}
                className="btn btn-primary btn-lg w-full"
              >
                Login to Purchase
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
